class Component(BaseComponent):
    name = "构建多轮对话"
    category = "大模型组件"
    description = "用于构建符合大模型输入格式的多轮对话消息列表，支持追加新消息。"
    requirements = "无特殊依赖，输出为标准 JSON 格式的对话列表。"

    inputs = [
        PortDefinition(name="history", label="输入1", type=ArgumentType.JSON, connection=ConnectionType.SINGLE),
    ]

    outputs = [
        PortDefinition(name="output1", label="输出1", type=ArgumentType.JSON),
    ]

    properties = {
        "prop1": PropertyDefinition(
            type=PropertyType.DYNAMICFORM,
            label="新增消息",
            schema={
                "role": {
                    "type": PropertyType.CHOICE.value,
                    "default": "user",
                    "label": "角色",
                    "choices": ["system", "user", "assistant"]
                },
                "content": {
                    "type": PropertyType.LONGTEXT.value,
                    "default": "",
                    "label": "内容",
                },
            }
        ),
    }

    def run(self, params, inputs = None):
        """
        params: 节点属性（来自UI）
        inputs: 上游输入（key=输入端口名）
        return: 输出数据（key=输出端口名）
        """
        # 获取历史对话（如果有的话）
        history = inputs.get("history") if inputs else None
        if history is None:
            history = []
        # 组件内直接使用全局变量
        model_name = self.global_variable.model_name

        # 验证 history 是否为 list
        if not isinstance(history, list):
            history = []
        self.logger.info(params.prop1)

        # 获取用户配置的新消息
        messages = history + [
            {
                "role": message.role,
                "content": message.content
            } for message in params.prop1
        ]

        # 返回符合大模型输入格式的对话列表
        return {
            "output1": messages  # 注意：必须与 outputs 中的 name 一致
        }

